mapper.select:
SELECT
*
FROM
`teacher_course` AS tc
LEFT JOIN student_course AS sc ON tc.teachCourseId = sc.teachCourseId
WHERE sc.studentAccountId = #{accountId}

    AND tc.schoolYear = #{schoolYear}


    AND tc.semester = #{semester}

ORDER BY endTime DESC

    LIMIT #{offset},#{limit}

SELECT
count(*)
FROM
`teacher_course` AS tc
LEFT JOIN student_course AS sc ON tc.teachCourseId = sc.teachCourseId
WHERE sc.studentAccountId = #{accountId}

    AND tc.schoolYear = #{schoolYear}


    AND tc.semester = #{semester}

SELECT
*
FROM
teacher_course AS tc
WHERE
teacherAccountId =#{accountId}

    AND tc.schoolYear = #{schoolYear}


    AND tc.semester = #{semester}

ORDER BY endTime DESC

    LIMIT #{offset},#{limit}

SELECT
count(*)
FROM
teacher_course AS tc
WHERE
teacherAccountId =#{accountId}

    AND tc.schoolYear = #{schoolYear}


    AND tc.semester = #{semester}

SELECT
*
FROM
teacher_course AS tc
WHERE 1=1

    AND tc.schoolYear = #{schoolYear}


    AND tc.semester = #{semester}

ORDER BY endTime DESC

    LIMIT #{offset},#{limit}

SELECT count(*) FROM teacher_course AS tc WHERE 1=1
AND tc.schoolYear = #{schoolYear}
AND tc.semester = #{semester}
